Event.TbScript:

Begin
      if p_CtrlValue != ''
        then p_CtrlValue = p_CtrlValue + ';' + p_HklValue;
        else p_CtrlValue = p_HklValue;
End

SelectionModes.Mode:

SELECT 
     Carrier              { COL MA_Carriers.Carrier }
     ,CompanyName         { COL MA_Carriers.CompanyName }
     ,Fax                 { COL MA_Carriers.Fax }
     ,Address             { COL MA_Carriers.Address }
     ,City                { COL MA_Carriers.City }
     ,ZIPCode             { COL MA_Carriers.ZIPCode }
     ,County              { COL MA_Carriers.County }
     ,Country             { COL MA_Carriers.Country }
     ,Notes               { COL MA_Carriers.Notes }
     ,Disabled            { COL MA_Carriers.Disabled } 
     ,ISOCountryCode      { COL MA_Carriers.ISOCountryCode }     
 FROM MA_Carriers
 WHERE 
    Carrier BETWEEN { IN ask_StartCode } AND  { IN ask_EndCode }
 ORDER BY CompanyName
