Event.TbScript:

Begin
      if p_CtrlValue != ''
        then p_CtrlValue = p_CtrlValue + ';' + p_HklValue;
        else p_CtrlValue = p_HklValue;
End

SelectionModes.Mode:

SELECT 
     Producer             { COL MA_Producers.Producer }
     ,CompanyName         { COL MA_Producers.CompanyName }
     ,EMail               { COL MA_Producers.EMail }
     ,Address             { COL MA_Producers.Address }
     ,City                { COL MA_Producers.City }
     ,ZIPCode             { COL MA_Producers.ZIPCode }
     ,County              { COL MA_Producers.County }
     ,Country             { COL MA_Producers.Country }
     ,ContactPerson       { COL MA_Producers.ContactPerson }
     ,WorkingTime         { COL MA_Producers.WorkingTime }  
     ,Notes               { COL MA_Producers.Notes }
     ,Disabled            { COL MA_Producers.Disabled }          
 FROM MA_Producers
 WHERE 
    EMail <> {''} AND
    Producer BETWEEN { IN ask_StartCode } AND  { IN ask_EndCode }
 ORDER BY CompanyName
