Function.TbScript:

string div = "<div style='width:" + format(width) + "px;text-align:" + align + ";";
div = div + (overflow ? "overflow:auto;" : "float:left;" );  
if fontColor != -1 then div = div + "color:" + Framework.TbWoormViewer.TbWoormViewer.H_ColorRGB(fontColor) + ";";
if bkgColor != -1 then div = div + "background-color:" + Framework.TbWoormViewer.TbWoormViewer.H_ColorRGB(bkgColor) + ";";
div = div + "'>" + txt + "</div>";
return div;
